// Code Node - Convert Markdown to HTML
const ideasJSON = $input.first().json.message.content.ideas;

let ideasHTML = '';
ideasJSON.forEach((idea, index) => {
  ideasHTML += `
    <h3>${index + 1}. ${idea.article_idea}</h3>
    <p>${idea.description}</p>
    <p>********************************</p>
  `;
});

const styledHTML = `
<!DOCTYPE html>
<html>
  <body>
      <h1>Article Ideas</h1>
      <div>
          ${ideasHTML}
      </div>
  </body>
</html>`;

return [{ json: { html: styledHTML } }];

